City());},onGetListDistrict:function onGetListDistrict(cityID){dispatch(AddressActions.onGetDistrict(cityID));},onGetListWard:function onGetListWard(cityID,districtID){dispatch(AddressActions.onGetWard(cityID,districtID));}};};export default connect(mapStateToProps,mapDispatchToProps)(RegisterPage);

